import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css'],
})
export class CarouselComponent implements OnInit {
  slides = [
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/10.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/02.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/03.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/04.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/05.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/06.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/07.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/09.png' },
    { img: 'https://www.casasbahia-imagens.com.br/criacao/01-home/icones-depto/2023/01-jan/27/26.png' }
  ];

  slideConfig = { slidesToShow: 6, slidesToScroll: 4, arrows: true };

  addSlide() {
    this.slides.push({ img: 'http://placehold.it/350x150/777777' });
  }

  removeSlide() {
    this.slides.length = this.slides.length - 1;
  }

  slickInit(e: any) {
    console.log('slick initialized');
  }

  breakpoint(e: any) {
    console.log('breakpoint');
  }

  afterChange(e: any) {
    console.log('afterChange');
  }

  beforeChange(e: any) {
    console.log('beforeChange');
  }

  constructor() {}

  ngOnInit(): void {}
}
